DROP TABLE TRADING_RECORD;
CREATE TABLE TRADING_RECORD(
    ID VARCHAR(32) NOT NULL   COMMENT 'ID' ,
    COIN_CODE VARCHAR(32)    COMMENT '币种' ,
    QUANTITY DECIMAL(32,10)    COMMENT '数量' ,
    TRADING_TYPE VARCHAR(32)    COMMENT '交易类型 1买入,2卖出' ,
    MONEY DECIMAL(32,8)    COMMENT '交易总价' ,
    UNIT VARCHAR(32)    COMMENT '交易单位 CNY,BTC,USD等' ,
    PRICE_TYPE VARCHAR(32)    COMMENT '价格类型 1总价,2单价' ,
    BOOKED_DATE DATE    COMMENT '交易日期' ,
    REMARK VARCHAR(512)    COMMENT '备注' ,
    REVISION INT    COMMENT '乐观锁' ,
    CREATED_BY VARCHAR(32)    COMMENT '创建人' ,
    CREATED_TIME DATETIME    COMMENT '创建时间' ,
    UPDATED_BY VARCHAR(32)    COMMENT '更新人' ,
    UPDATED_TIME DATETIME    COMMENT '更新时间' ,
    PRIMARY KEY (ID)
) COMMENT = '买卖记录 买卖记录';
